mapper.select:
SELECT d.*,t.typeVideoName FROM video_data d INNER JOIN videotype t ON d.typeId = t.typeId WHERE 1=1
AND d.videoTitle LIKE CONCAT('%',#{videoTitle},'%')
AND t.typeId = #{typeId}
ORDER BY d.uploadTime DESC LIMIT #{from},#{pageSize}

SELECT count(1) FROM video_data d INNER JOIN videotype t ON d.typeId = t.typeId WHERE 1=1
AND d.videoTitle LIKE CONCAT('%',#{videoTitle},'%')
AND t.typeId = #{typeId}
