mapper.update:
UPDATE order.order_info
   SET
   STATUS = #{status}
   WHERE
   id = #{id};

mapper.select:
SELECT * FROM order_info


        and userId = #{uid}


        and goodId = #{gid}


        and createDate Between #{startDate} and #{endDate}


ORDER BY updateDate DESC
LIMIT #{from},#{pageSize}

SELECT count(1) FROM order_info


       and userId = #{uid}


       and goodId = #{gid}


       and createDate Between #{startDate} and #{endDate}

SELECT * FROM order_info WHERE userId = #{uid}
ORDER BY updateDate DESC
LIMIT #{from},#{pageSize}
